/**
 * @file 将数组转为以指定字段为 key 的 Object
 * @author picheng@sensorsdata.cn
 */

/**
 * @param {Array.<Object>} arr 要转为 Object 的数组
 * @param {string} key 以哪个字段为 key
 */
export default (arr, key) => {
  const data = {};
  arr.forEach(item => {
    if (!item[key]) {
      return;
    }
    data[item[key]] = item;
  });
  return data;
}