
import cron from '../src/cron'
describe('cron 表达式相关方法', function() {
  test('cronToText', () => {
    let str = '0 0 0 * * ? *';
    expect(cron.cronToText(str)).toBe('每天 00:00')
    str = '0 32 3 * * ? *';
    expect(cron.cronToText(str)).toBe('每天 03:32')
    str = '0 32 3,14 * * ? *';
    expect(cron.cronToText(str)).toBe('每天 03:32，14:32')
    str = '0 32,42 3,14 * * ? *';
    expect(cron.cronToText(str)).toBe('每天 03:32，03:42，14:32，14:42')
  });
  test('toCron', () => {
    let obj = {
      unit: 'day',
      week: [],
      day: [],
      time: ['00:00', '01:00']
    };
    expect(cron.toCron(obj)).toBe('0 0 0,1 * * ? *')
  })
});
