/**
 * @file 部分基数词转序数词的方法
 * @author huangtaizhao@sensorsdata.cn
 */

 /**
 * @param {*} pharal           中文序数词
 * @param {number} cardinal    基数
 * @param {string} ordinal     序数
 * @return {string}
 */
import getEnv from './getEnv';
const env = getEnv();
const isNotEn = env !== 'en-us';

export function cardinalToOrdinal(pharal, cardinal, ordinal){
    if(isNotEn){
        return pharal;
    }
    return `${ordinal} ${cardinal}`;
}

 /**
 * @param {number} num    基数
 * @return {string}
 */

/**
 * 1, 2, 3, 4, 5 ... 10
 * =>
 * 1st, 2nd, 3rd, 4th, 5th ... 10th
*/
export function cardinalToShortOrdinal(num){
    if(isNotEn) return num;
    return num + (['st', 'nd', 'rd'][num < 20 ? num - 1 : num % 10 - 1] || 'th');
}